ROOT.GetLastACLineDateTime:

  select max(CREATE_TIME) as CREATE_TIME from 
same:SearchDistrictCommonSql:SchemaName
SG_DEV_ACLINE_H1_MEA_{0}

ROOT.GetLastDCLineDateTime:

  select max(CREATE_TIME) as CREATE_TIME from 
same:SearchDistrictCommonSql:SchemaName
SG_DEV_DCLINE_H1_MEA_{0}

ROOT.GetLastACLineH1MEA:
    select SG_DEV_ACLINE_H1_MEA_{0}.*, SG_DEV_ACLINEEND_B.LINE_ID,SG_DEV_ACLINE_B.VOLTAGE_TYPE
    from SG_DEV_ACLINE_H1_MEA_{0}
join SG_DEV_ACLINEEND_B on SG_DEV_ACLINE_H1_MEA_{0}.ACLINEEND_ID = SG_DEV_ACLINEEND_B.ID
 join SG_DEV_ACLINE_B ON SG_DEV_ACLINEEND_B.LINE_ID= SG_DEV_ACLINE_B.ID {1}

ROOT.GetLastDCLineH1MEA:
    select SG_DEV_DCLINE_H1_MEA_{0}.*, SG_DEV_DCLINEEND_B.LINE_ID,SG_DEV_DCLINE_B.VOLTAGE_TYPE 
    from SG_DEV_DCLINE_H1_MEA_{0}
join SG_DEV_DCLINEEND_B on SG_DEV_DCLINE_H1_MEA_{0}.DCLINEEND_ID = SG_DEV_DCLINEEND_B.ID
 join SG_DEV_DCLINE_B ON SG_DEV_DCLINEEND_B.LINE_ID= SG_DEV_DCLINE_B.ID {1}

ROOT.GetLineRealTimeMEA:
select a.*,LINE_ID AS ID FROM 
same:SearchDistrictCommonSql:SchemaName
YSH_LINE_REALTIME_MEA a {0}

ROOT.DelACLineH1MEA:

delete from 
same:SearchDistrictCommonSql:SchemaName
YSH_LINE_REALTIME_MEA where ID={0}
