ROOT.GetTreeNode:
  
    select ID,NAME_ABBREVIATION from 

  same:SearchCommonSql:SchemaName
  SG_CON_PWRGRID_B where level = 1003 and parent_ID={0}

ROOT.GetUserInfo:

  select NAME from 

same:SearchCommonSql:SchemaName
SG_ORG_EMPLOYEE_B where id={0}

ROOT.GetUserID:

  select USER_ID from 

same:SearchCommonSql:SchemaName
SG_SYS_UA_USER_B where USER_NAME={0}

SaveSettings.mapType:

DELETE FROM 
same:SearchDistrictCommonSql:SchemaName
YSH_MAP_CONFIG_USER where configname='mapType' AND userid={0};
INSERT INTO 
  same:SearchDistrictCommonSql:SchemaName
  YSH_MAP_CONFIG_USER (configname,value,userid) VALUES ('mapType',{1},{0});

