ROOT.GetSceneConfig:
  SELECT CFGKEY,CFGVALUE FROM 
same:SearchDistrictCommonSql:SchemaAppName
YSH_SCENE_SETTINGS WHERE ID={0}

ROOT.CheckScene:
  SELECT 1 FROM 
same:SearchDistrictCommonSql:SchemaAppName
YSH_SCENE_INFO WHERE NAME={0} AND USERID={1}
