        this.getData = function() {
          var names = [];
          var values = [];
          if (this.rundata) {
            for (var i = 0;i < this.rundata.length;i++) {
              var item = this.rundata[i];
              names.push(item[0]);
              values.push(item[1]);
            }
          }
          this.const10 = names;
          this.const11 = values;
        }
        
