this.devices = Ysh.Object.clone(this.ds0);
this.filterStation = function() {
   var s = this.strKey;
   if (s == "") {
     this.ds0 = Ysh.Object.clone(this.devices);
     return;
   }
   var arr = [];
   for (var i = 0;i < this.devices.length;i++) {
      var d = this.devices[i];
      if (d[3].indexOf(s) >= 0) {
        arr.push(d);
      }
   }
   this.ds0 = arr;
}
