ROOT.GetAcLineStations:
  SELECT START_ST_ID,END_ST_ID,START_TOWER_ID,END_TOWER_ID,NAME FROM 
same:SearchCommonSql:SchemaName
SG_DEV_ACLINE_B WHERE ID={0}

ROOT.GetDcLineStations:
SELECT START_ST_ID,END_ST_ID,START_TOWER_ID,END_TOWER_ID,NAME FROM 
same:SearchCommonSql:SchemaName
SG_DEV_DCLINE_B WHERE ID={0}

ROOT.GetAcLineSameTower:
    select t1.tower_id,t1.line_id,t1.towername,t1.towerno from same:SearchCommonSql:SchemaNamesg_dev_acline_r_tower t1 
    --JOIN same:SearchCommonSql:SchemaNamesg_dev_acline_r_tower t2 on t1.tower_id=t2.tower_id and t1.line_id<>t2.line_id 
where 1=0 AND t1.line_id={0}

ROOT.GetDcLineSameTower:
    select t1.tower_id,t1.line_id,t1.towername,t1.towerno from same:SearchCommonSql:SchemaNamesg_dev_dcline_r_tower t1 
    --JOIN same:SearchCommonSql:SchemaNamesg_dev_dcline_r_tower t2 on t1.tower_id=t2.tower_id and t1.line_id<>t2.line_id 
where 1=0 AND t1.line_id={0}

ROOT.GetAcLineSameTower0:
    select t1.tower_id,t2.line_id,t2.towername,t2.towerno from same:SearchCommonSql:SchemaNamesg_dev_acline_r_tower t1 
    JOIN same:SearchCommonSql:SchemaNamesg_dev_acline_r_tower t2 on t1.tower_id=t2.tower_id and t1.line_id<>t2.line_id 
where t1.line_id={0}

ROOT.GetDcLineSameTower0:
    select t1.tower_id,t2.line_id,t2.towername,t2.towerno from same:SearchCommonSql:SchemaNamesg_dev_dcline_r_tower t1 
    JOIN same:SearchCommonSql:SchemaNamesg_dev_dcline_r_tower t2 on t1.tower_id=t2.tower_id and t1.line_id<>t2.line_id 
where t1.line_id={0}

ROOT.GetEffectLineTowerFore:
    
select a.warntype,a.warntime,a.lineid,a.id,a.num as tno,'','',a.warnlevel 
from same:SearchDistrictCommonSql:SchemaAppNameysh_weather_alarm_weather_tower_forecast a
where a.warntime>={0} and a.warntime<{1}
