bk.GetLineTower:
    
    select alarm_tower,alarm_time,longitude,latitude,alarm_position,distence from 
    same:SearchCommonSql:SchemaName
    sg_com_mountain_h_firealarm_{3}
where alarm_line={0} AND alarm_time>={1} and alarm_time<{2}

bk.GetAllEffectList:
    
select station_id,is_forecast,alarm_time from same:SearchDistrictCommonSql:SchemaAppNameysh_weather_alarm_lightning_station where alarm_time>={0} and alarm_time<{1}
UNION ALL
SELECT DISTINCT line_id,is_forecast,alarm_time FROM same:SearchDistrictCommonSql:SchemaAppNameysh_weather_alarm_lightning_tower where alarm_time>={0} and alarm_time<{1}

bk.GetLightCountP:
select province_owner,province,count(1) c from same:SearchDistrictCommonSql:SchemaAppNameysh_weather_alarm_fire where alarm_time>={0} and alarm_time<{1} 
same:SkipSome
group by province_owner,province order by c desc

bk.GetLightCountPTime:
    select tm,count(1) as c from (
SELECT to_char(alarm_time,'yyyy-mm-dd hh:mi:')||'00' as tm FROM same:SearchDistrictCommonSql:SchemaAppNameysh_weather_alarm_fire where province_owner={2} and alarm_time>={0} and alarm_time<{1} 
) a group by tm order by tm

bk.GetLightCountCTime:
    select tm,count(1) as c from (
SELECT to_char(alarm_time,'yyyy-mm-dd hh:mi:')||'00' as tm FROM same:SearchDistrictCommonSql:SchemaAppNameysh_weather_alarm_fire where city_owner={2} and alarm_time>={0} and alarm_time<{1} 
) a group by tm order by tm

bk.GetLightCountDTime:
    select tm,count(1) as c from (
SELECT to_char(alarm_time,'yyyy-mm-dd hh:mi:')||'00' as tm FROM same:SearchDistrictCommonSql:SchemaAppNameysh_weather_alarm_fire where district_owner={2} and alarm_time>={0} and alarm_time<{1} 
) a group by tm order by tm

bk.GetLightCountAllTime:
    select tm,count(1) as c from (
SELECT to_char(alarm_time,'yyyy-mm-dd hh:mi:')||'00' as tm FROM same:SearchDistrictCommonSql:SchemaAppNameysh_weather_alarm_fire where alarm_time>={0} and alarm_time<{1} 
    same:SkipSome
    
) a group by tm order by tm

bk.GetLightAllTime:
    
SELECT alarm_time,longitude,latitude,1 as alarm_level FROM same:SearchDistrictCommonSql:SchemaAppNameysh_weather_alarm_fire where alarm_time>={0} and alarm_time<{1} 
    same:SkipSome
     order by alarm_time

bk.GetEffectLine:
  select alarm_line,alarm_tower,0 as is_forecast,alarm_level from 
same:SearchCommonSql:SchemaName
sg_com_mountain_h_firealarm_{{2}} where alarm_time>={0} and alarm_time<{1}
