ROOT.GetLineTowerCount:
    select max(c) from (
SELECT COUNT(1) AS C FROM 
    same:SearchCommonSql:SchemaName
    sg_dev_acline_r_tower where LINE_ID={0}
union  
SELECT COUNT(1) AS C FROM 
    same:SearchCommonSql:SchemaName
    sg_dev_dcline_r_tower where LINE_ID={0}
) a

ROOT.GetSameTowerInfo:
select TOWER_ID,TOWER_STRNO,SAMETWR_LINE_ID,SAMETWR_TOWER_ID,SAMETWR_TOWER_STRNO 
from same:SearchDistrictCommonSql:SchemaAppNameYSH_LINE_R_SAMETOWER S WHERE LINE_ID={0}
ORDER BY SAMETWR_LINE_ID

ROOT.GetAcLineSETower:
SELECT start_tower_id,end_tower_id,name,start_st_id,end_st_id FROM 
same:SearchCommonSql:SchemaName
SG_DEV_ACLINE_B WHERE id={0}

ROOT.GetDcLineSETower:
SELECT start_tower_id,end_tower_id,name,start_st_id,end_st_id FROM 
same:SearchCommonSql:SchemaName
SG_DEV_DCLINE_B WHERE id={0}

ROOT.AddAcLineSETower:

   update SG_DATACENTER.SG_DEV_ACLINE_B 
   SET start_tower_id={0}  , end_tower_id={1} , STAMP = replace(STAMP ,substr(STAMP,-19),'')||{2}
   where id={3}
