bk1.GetAcList:
    select line_id,line_name,st_tower_no,end_tower_no,reporter,voltage_name,icing_type, icing_thick_design,icing_thickness,'' as sf,date_time
from 
    same:SearchDistrictCommonSql:SchemaAppName
    sg_dev_acline_h_icinglive_{{0}}
    WHERE date_time>={1} and date_time<{2}

bk1.GetDcList:
    select line_id,line_name,st_tower_no,end_tower_no,reporter,voltage_name,icing_type, icing_thick_design,icing_thickness,'' as sf,date_time
from 
    same:SearchDistrictCommonSql:SchemaAppName
    sg_dev_dcline_h_icinglive_{{0}}
    WHERE date_time>={1} and date_time<{2}

bk1.GetLineTower:
    
    select st_tower_id,end_tower_id from 
    same:SearchDistrictCommonSql:SchemaAppName
    sg_dev_acline_h_icinglive_{{0}}
where line_id={0} AND date_time>={1} and date_time<{2}

bk.GetLineTower:
      
    select alarm_tower,alarm_time,longitude,latitude from 
      same:SearchCommonSql:SchemaName
      sg_dev_acline_h_icinglive_{{0}}
where line_id={0} AND date_time>={1} and date_time<{2}
