ROOT.GetAllEffectList:
    
select station_id,is_forecast,date_time from same:SearchDistrictCommonSql:SchemaAppNameysh_weather_alarm_lightning_station where date_time>={0} and date_time<{1}
UNION ALL
SELECT DISTINCT line_id,is_forecast,date_time FROM same:SearchDistrictCommonSql:SchemaAppNameysh_weather_alarm_lightning_tower where date_time>={0} and date_time<{1}

ROOT.GetLightCountPBK:
select province_owner,province,count(1) c from same:SearchDistrictCommonSql:SchemaAppNameysh_weather_lightning_district where date_time>={0} and date_time<{1} 
same:SkipSome
group by province_owner,province order by c desc

ROOT.GetLightCountPTime:
    select tm,count(1) as c from (
SELECT to_char(date_time,'yyyy-mm-dd hh:mi:')||'00' as tm FROM same:SearchDistrictCommonSql:SchemaAppNameysh_weather_lightning_district where province_owner={2} and date_time>={0} and date_time<{1} 
) a group by tm order by tm

ROOT.GetLightCountCTime:
    select tm,count(1) as c from (
SELECT to_char(date_time,'yyyy-mm-dd hh:mi:')||'00' as tm FROM same:SearchDistrictCommonSql:SchemaAppNameysh_weather_lightning_district where city_owner={2} and date_time>={0} and date_time<{1} 
) a group by tm order by tm

ROOT.GetLightCountDTime:
    select tm,count(1) as c from (
SELECT to_char(date_time,'yyyy-mm-dd hh:mi:')||'00' as tm FROM same:SearchDistrictCommonSql:SchemaAppNameysh_weather_lightning_district where district_owner={2} and date_time>={0} and date_time<{1} 
) a group by tm order by tm

ROOT.GetLightCountAllTime:
    select tm,count(1) as c from (
SELECT to_char(date_time,'yyyy-mm-dd hh:mi:')||'00' as tm FROM same:SearchDistrictCommonSql:SchemaAppNameysh_weather_lightning_district where date_time>={0} and date_time<{1} 
    same:SkipSome
    
) a group by tm order by tm

ROOT.GetLightAllTimeBK:
    
SELECT date_time,longitude,latitude,peak_current FROM same:SearchDistrictCommonSql:SchemaAppNameysh_weather_lightning_district where date_time>={0} and date_time<{1} 
    same:SkipSome
     order by date_time

ROOT.GetEffectLine:
SELECT line_id,2 as affect_type,0,date_time 
from same:SearchDistrictCommonSql:SchemaAppNameSG_DEV_ACLINE_H_LIGHTNINGLIVE_2022 
where date_time<{1} AND date_time>{0}
