ROOT.GetName:
SELECT NAME FROM 
same:SearchCommonSql:SchemaName
SG_CON_SECTION_B WHERE ID={0}

ROOT.GetSectionMembers:
    SELECT R.DEV_ID,D.NAME,'' AS ST_ID,R.RATIO FROM same:SearchCommonSql:SchemaNameSG_CON_SECTION_R_CONSTITUTE R JOIN same:SearchCommonSql:SchemaNameSG_DEV_ACLINE_B D ON R.DEV_ID=D.ID WHERE R.ID={0}
UNION ALL
SELECT R.DEV_ID,D.NAME,'' AS ST_ID,R.RATIO FROM same:SearchCommonSql:SchemaNameSG_CON_SECTION_R_CONSTITUTE R JOIN same:SearchCommonSql:SchemaNameSG_DEV_DCLINE_B D ON R.DEV_ID=D.ID WHERE R.ID={0}
UNION ALL
SELECT R.DEV_ID,D.NAME,D.ST_ID,R.RATIO FROM same:SearchCommonSql:SchemaNameSG_CON_SECTION_R_CONSTITUTE R JOIN same:SearchCommonSql:SchemaNamesg_dev_pwrtransfm_b D ON R.DEV_ID=D.ID WHERE R.ID={0}
UNION ALL
SELECT R.DEV_ID,D.NAME,D.ST_ID,R.RATIO FROM same:SearchCommonSql:SchemaNameSG_CON_SECTION_R_CONSTITUTE R JOIN same:SearchCommonSql:SchemaNameSG_DEV_GENERATOR_B D ON R.DEV_ID=D.ID WHERE R.ID={0}
UNION ALL
SELECT R.DEV_ID,D.NAME,D.ST_ID,R.RATIO FROM same:SearchCommonSql:SchemaNameSG_CON_SECTION_R_CONSTITUTE R JOIN same:SearchCommonSql:SchemaNameSG_CON_CONVERTER_B D ON R.DEV_ID=D.ID WHERE R.ID={0}

ROOT.GetSectionHisMeas:
    SELECT ID,CREATE_TIME,MEAS_TYPE,V00,V05,V10,V15,V20,V25,V30,V35,V40,V45,V50,V55
FROM same:SearchCommonSql:SchemaNamesg_con_section_h1_mea_{{0}}
WHERE CREATE_TIME>={1} AND CREATE_TIME<{2}
and meas_type in ('00002001','00002020','00002021') --and (id='04019901000028' or id='04019901000001')
ORDER BY CREATE_TIME

ROOT.GetSectionHisMeas1:
    SELECT ID,CREATE_TIME,MEAS_TYPE,V00,V01,V02,V03,V04,V05,V06,V07,V08,V09,V10,V11,V12,V13,V14,V15,V16,V17,V18,V19,V20,V21,V22,V23,V24,V25,V26,V27,V28,V29,V30,V31,V32,V33,V34,V35,V36,V37,V38,V39,V40,V41,V42,V43,V44,V45,V46,V47,V48,V49,V50,V51,V52,V53,V54,V55,V56,V57,V58,V59
FROM same:SearchCommonSql:SchemaNamesg_con_section_h1_mea_{{0}}
WHERE CREATE_TIME>={1} AND CREATE_TIME<{2}
and meas_type in ('00002001','00002020','00002021') and (id='04019901000028' or id='04019901000001')
ORDER BY CREATE_TIME
