ROOT.GetTyphoonline:
    SELECT NVL(R.ID,F.ID) AS ID,R.TM AS REALTIME,F.TM AS FORETIME FROM (
SELECT AFFECT_OBJ_ID AS ID,MIN(START_TIME) AS TM FROM same:SearchDistrictCommonSql:SchemaAppNameYSH_TYPHOON_AFFECT a
where AFFECT_TYPE=2 AND TYPHOON_ID={0} AND IS_FORECAST=0
GROUP BY AFFECT_OBJ_ID
) R FULL JOIN (
SELECT AFFECT_OBJ_ID AS ID,MIN(START_TIME) AS TM FROM same:SearchDistrictCommonSql:SchemaAppNameYSH_TYPHOON_AFFECT a
where AFFECT_TYPE=2 AND TYPHOON_ID={0} AND IS_FORECAST={1}
GROUP BY AFFECT_OBJ_ID
) F ON R.ID=F.ID

ROOT.GetTyphoonBDZ:
    SELECT NVL(R.ID,F.ID) AS ID,R.TM AS REALTIME,F.TM AS FORETIME FROM (
SELECT AFFECT_OBJ_ID AS ID,MIN(START_TIME) AS TM FROM same:SearchDistrictCommonSql:SchemaAppNameYSH_TYPHOON_AFFECT a
where AFFECT_TYPE=1 AND TYPHOON_ID={0} AND IS_FORECAST=0
GROUP BY AFFECT_OBJ_ID
) R FULL JOIN (
SELECT AFFECT_OBJ_ID AS ID,MIN(START_TIME) AS TM FROM same:SearchDistrictCommonSql:SchemaAppNameYSH_TYPHOON_AFFECT a
where AFFECT_TYPE=1 AND TYPHOON_ID={0} AND IS_FORECAST={1}
GROUP BY AFFECT_OBJ_ID
) F ON R.ID=F.ID

ROOT.GetTyphoonDC:
    SELECT NVL(R.ID,F.ID) AS ID,R.TM AS REALTIME,F.TM AS FORETIME FROM (
SELECT AFFECT_OBJ_ID AS ID,MIN(START_TIME) AS TM FROM same:SearchDistrictCommonSql:SchemaAppNameYSH_TYPHOON_AFFECT a
where AFFECT_TYPE=3 AND TYPHOON_ID={0} AND IS_FORECAST=0
GROUP BY AFFECT_OBJ_ID
) R FULL JOIN (
SELECT AFFECT_OBJ_ID AS ID,MIN(START_TIME) AS TM FROM same:SearchDistrictCommonSql:SchemaAppNameYSH_TYPHOON_AFFECT a
where AFFECT_TYPE=3 AND TYPHOON_ID={0} AND IS_FORECAST={1}
GROUP BY AFFECT_OBJ_ID
) F ON R.ID=F.ID

ROOT.GetZyxl:
    SELECT NVL(R.ID,F.ID) AS ID,R.START_TIME,F.START_TIME FROM (
SELECT AFFECT_OBJ_ID AS ID,MIN(START_TIME) AS START_TIME FROM YSH_TYPHOON_AFFECT WHERE AFFECT_TYPE=2 AND TYPHOON_ID='{0}' AND IS_FORECAST=0 GROUP BY AFFECT_OBJ_ID
) R FULL JOIN (
SELECT AFFECT_OBJ_ID AS ID,MIN(START_TIME) AS START_TIME FROM YSH_TYPHOON_AFFECT WHERE AFFECT_TYPE=2 AND TYPHOON_ID='{0}' AND IS_FORECAST=1 GROUP BY AFFECT_OBJ_ID
) F ON R.ID=F.ID

ROOT.GetAllEffectList:
    SELECT AFFECT_TYPE,AFFECT_OBJ_ID,0 AS F FROM same:SearchDistrictCommonSql:SchemaAppNameYSH_TYPHOON_AFFECT where TYPHOON_ID={0} AND IS_FORECAST=0-- AND START_TIME<={1}
UNION ALL
SELECT AFFECT_TYPE,AFFECT_OBJ_ID,1 FROM same:SearchDistrictCommonSql:SchemaAppNameYSH_TYPHOON_AFFECT where TYPHOON_ID={0} AND IS_FORECAST={2}-- AND START_TIME>{1}

ROOT.GetTimeEffect:
select affect_type,affect_obj_id,typhoon_radius
from same:SearchDistrictCommonSql:SchemaAppNameYSH_TYPHOON_AFFECT 
where typhoon_id={0} and start_time<={1} and end_time>={1} and is_forecast={2}
