ROOT.GetGridMaxPower0:
    SELECT ID,MAX_VALUE/10 FROM same:SearchCommonSql:SchemaNamesg_org_dcc_h_sta_pow_{0} 
WHERE DATASOURCE_ID='0021990100' AND CREATE_DATE=TO_DATE({1},'yyyy-MM-dd') AND MEAS_TYPE='13022001' AND ID='0021990100'
UNION ALL
SELECT ID,MAX_VALUE/10 FROM same:SearchCommonSql:SchemaNameSG_CON_PWRGRID_H_STA_POW_{0} 
WHERE DATASOURCE_ID='0021990100' AND CREATE_DATE=TO_DATE({1},'yyyy-MM-dd') AND MEAS_TYPE='13022001' AND ID IN ('0101990101','0101990102','0101990103','0101990104','0101990105','0101990106')

ROOT.GetRunoffresvData:
    
    SELECT ID,NAME,M1V,M2V,M3V,M4V,M5V,M6V,M7V,PLANT_ID,M8V,M9V,M10V,RTYPE,RID FROM (
    select R.ID,R.NAME,M1.MEAS_VALUE AS M1V,M2.MEAS_VALUE AS M2V,M3.MEAS_VALUE AS M3V,M4.MAX_VALUE/10 AS M4V,M5.TMR_GEN_VALUE/10 AS M5V,M6.MEAS_VALUE*10000 AS M6V,M7.MEAS_VALUE*100 AS M7V,R.PLANT_ID,NULL::DECIMAL AS M8V,B.DEAD_WATER_LEVEL AS M9V,NULL::DECIMAL AS M10V,R.ORDERINDEX,RTYPE,RID
    from YS_DB.YSH_RUNOFFRESV_R_DCC R 
LEFT JOIN same:SearchCommonSql:SchemaNamesg_com_runoffresv_h_flood_{1} M1 ON R.ID=M1.ID AND M1.meas_type='00001006'  AND M1.DATE={3}
LEFT JOIN (SELECT ID,AVG(MEAS_VALUE) AS MEAS_VALUE FROM same:SearchCommonSql:SchemaNamesg_com_runoffresv_h_flood_{1} WHERE DATE>={2} AND DATE<{3} AND MEAS_TYPE='00001016' GROUP BY ID) M2 ON R.ID=M2.ID
LEFT JOIN (SELECT ID,AVG(MEAS_VALUE) AS MEAS_VALUE FROM same:SearchCommonSql:SchemaNamesg_com_runoffresv_h_flood_{1} WHERE DATE>={2} AND DATE<{3} AND MEAS_TYPE='00001015' GROUP BY ID) M3 ON R.ID=M3.ID
LEFT JOIN same:SearchCommonSql:SchemaNamesg_con_plant_h_sta_pow_{1} M4 ON R.RID=M4.ID AND M4.MEAS_TYPE='30022001' AND M4.create_date=TO_DATE({2},'yyyy-MM-dd') AND M4.DATASOURCE_ID='0021990100'
LEFT JOIN same:SearchCommonSql:SchemaNamesg_con_plant_h_sta_qua_{1} M5 ON R.RID=M5.ID AND M5.DATE=TO_DATE({2},'yyyy-MM-dd') AND M5.DATASOURCE_ID=R.DCC_ID
LEFT JOIN same:SearchCommonSql:SchemaNamesg_com_runoffresv_h_flood_{1} M6 ON R.ID=M6.ID AND M6.meas_type='00001022'  AND M6.DATE={3}
LEFT JOIN same:SearchCommonSql:SchemaNamesg_com_runoffresv_h_flood_{1} M7 ON R.ID=M7.ID AND M7.meas_type='00001024'  AND M7.DATE={3}
LEFT JOIN same:SearchCommonSql:SchemaNamesg_com_runoffresv_b B ON R.ID=B.ID
WHERE RTYPE=1 AND R.DCC_ID={0}
UNION ALL
select R.ID,R.NAME,M1.MEAS_VALUE,M2.MEAS_VALUE,M3.MEAS_VALUE,M4.MAX_VALUE/10,M5.FORWARD_VALUE/10,M6.MEAS_VALUE*10000,M7.MEAS_VALUE*100,R.PLANT_ID,NULL::DECIMAL,B.DEAD_WATER_LEVEL,NULL::DECIMAL,R.ORDERINDEX,RTYPE ,RID
from YS_DB.YSH_RUNOFFRESV_R_DCC R 
LEFT JOIN same:SearchCommonSql:SchemaNamesg_com_runoffresv_h_flood_{1} M1 ON R.ID=M1.ID AND M1.meas_type='00001006'  AND M1.DATE={3}
LEFT JOIN (SELECT ID,AVG(MEAS_VALUE) AS MEAS_VALUE FROM same:SearchCommonSql:SchemaNamesg_com_runoffresv_h_flood_{1} WHERE DATE>={2} AND DATE<{3} AND MEAS_TYPE='00001016' GROUP BY ID) M2 ON R.ID=M2.ID
LEFT JOIN (SELECT ID,AVG(MEAS_VALUE) AS MEAS_VALUE FROM same:SearchCommonSql:SchemaNamesg_com_runoffresv_h_flood_{1} WHERE DATE>={2} AND DATE<{3} AND MEAS_TYPE='00001015' GROUP BY ID) M3 ON R.ID=M3.ID
LEFT JOIN same:SearchCommonSql:SchemaNamesg_con_section_h_sta_pow_{1} M4 ON R.RID=M4.ID AND M4.MEAS_TYPE='00002001' AND  M4.create_date=TO_DATE({2},'yyyy-MM-dd')
LEFT JOIN same:SearchCommonSql:SchemaNamesg_con_section_h_sta_qua_{1} M5 ON R.RID_P=M5.ID AND  M5.date=TO_DATE({2},'yyyy-MM-dd')
LEFT JOIN same:SearchCommonSql:SchemaNamesg_com_runoffresv_h_flood_{1} M6 ON R.ID=M6.ID AND M6.meas_type='00001022'  AND M6.DATE={3}
LEFT JOIN same:SearchCommonSql:SchemaNamesg_com_runoffresv_h_flood_{1} M7 ON R.ID=M7.ID AND M7.meas_type='00001024'  AND M7.DATE={3}
LEFT JOIN same:SearchCommonSql:SchemaNamesg_com_runoffresv_b B ON R.ID=B.ID
WHERE RTYPE=2 AND R.DCC_ID={0}
UNION ALL
select ID,NAME,NULL::DECIMAL,NULL::DECIMAL,NULL::DECIMAL,NULL::DECIMAL,NULL::DECIMAL,NULL::DECIMAL,NULL::DECIMAL,PLANT_ID,NULL::DECIMAL,NULL::DECIMAL,NULL::DECIMAL,99999,RTYPE,RID
FROM YS_DB.YSH_RUNOFFRESV_R_DCC WHERE RTYPE=3 AND DCC_ID={0}
) A ORDER BY ORDERINDEX
