ROOT.GetGenStatData:
    select * from (
select nvl(a.grid,b.grid) as grid,nvl(a.plant_type,b.plant_type) as plant_type,nvl(a.y,b.y) as syear,nvl(a.m,b.m) as smonth,nvl(a.c,0) as rcount,nvl(b.c,0) as ecount from (
 select grid,plant_type,y,m,count(1) c from (
 select p.connective_pg_id as grid,p.plant_type, year(g.operate_date) as y,month(g.operate_date) as m from same:SearchCommonSql:SchemaNamesg_dev_generator_b g
 join same:SearchCommonSql:SchemaNamesg_con_plant_b p on g.st_id=p.id
 where g.operate_date is not null
) group by grid,plant_type,y,m
) a full join (
 select grid,plant_type,y,m,count(1) c from (
 select p.connective_pg_id as grid,p.plant_type, year(g.expiry_date) as y,month(g.expiry_date) as m from same:SearchCommonSql:SchemaNamesg_dev_generator_b g
 join same:SearchCommonSql:SchemaNamesg_con_plant_b p on g.st_id=p.id
 where g.expiry_date is not null
) group by grid,plant_type,y,m
) b on a.grid=b.grid and a.plant_type=b.plant_type and a.y=b.y and a.m=b.m
) order by plant_type,SYEAR,SMONTH

ROOT.test:
    insert into ysh_rpt_trans_cur (voltage_type,station_count,transfm_count,transformation_capacity)
SELECT NVL(A.VOLTAGE_TYPE,B.VOLTAGE_TYPE) AS VOLTAGE_TYPE,NVL(B.STATIONS,0),NVL(A.C,0),NVL(A.CAP,0) FROM (
SELECT VOLTAGE_TYPE,COUNT(1) AS C,SUM(MVA_RATE) AS CAP FROM same:SearchCommonSql:SchemaNamesg_dev_pwrtransfm_b
WHERE RUNNING_STATE=1003 AND VOLTAGE_TYPE IS NOT NULL
GROUP BY VOLTAGE_TYPE
) A FULL JOIN (
SELECT VOLTAGE_TYPE,SUM(C) AS STATIONS FROM (
SELECT TOP_AC_VOLTAGE_TYPE AS VOLTAGE_TYPE,COUNT(1) AS C FROM same:SearchCommonSql:SchemaNameSG_CON_SUBSTATION_B WHERE OPERATE_STATE=1003
GROUP BY TOP_AC_VOLTAGE_TYPE
UNION ALL
SELECT DC_VOLTAGE_TYPE,COUNT(1) FROM same:SearchCommonSql:SchemaNameSG_CON_CONVERSUBSTATION_B WHERE OPERATE_STATE=1003
GROUP BY DC_VOLTAGE_TYPE
) GROUP BY VOLTAGE_TYPE
) B ON A.VOLTAGE_TYPE=B.VOLTAGE_TYPE;

    
 insert into ysh_dev_station_s_trans
 (voltage_type,syear,smonth,rcount,ecount,rcap,ecap)
 select nvl(a.voltage_type,b.voltage_type),nvl(a.y,b.y),nvl(a.m,b.m),nvl(a.c,0),nvl(b.c,0),0,0 from (
 select voltage_type,y,m,count(1) c from (
 select g.voltage_type, year(g.operate_date) as y,month(g.operate_date) as m from same:SearchCommonSql:SchemaNamesg_dev_pwrtransfm_b g
 where g.operate_date is not null and g.voltage_type is not null
) group by voltage_type,y,m
) a full join (
 select voltage_type,y,m,count(1) c from (
 select g.voltage_type, year(g.expiry_date) as y,month(g.expiry_date) as m from same:SearchCommonSql:SchemaNamesg_dev_pwrtransfm_b g
 where g.expiry_date is not null and g.voltage_type is not null
) group by voltage_type,y,m
) b on a.voltage_type=b.voltage_type and a.y=b.y and a.m=b.m;
